    private void addTab(){
        addTranfer addTranfer = new addTranfer(budgetManager);
        tab.addTab("Tranfer",addTranfer);
    }
    private void clearText(){
        amount.setText(null);
        dateTime.setDate(null);
        note.setText(null);
    }
    private void hideLabel(){
        dateTime.setDate(new Date());
        addButton.setEnabled(false);
        addButton.setFocusable(false);
        checkError.setVisible(false);
        checkError.setForeground(Color.RED);
        error.setVisible(false);
        error.setForeground(Color.RED);
    }
   private boolean checkNull(){
        if(amount.getText().equals(""))
            return false;
        return true;
    }
    private boolean checkError(){
        return budgetManager.isFloat(amount.getText());
    }

